import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute, Params } from '@angular/router';
import { TaskService } from '../task.service';
import List from '../models/list';
import Task from '../models/task';
import { items } from './dashboard';
import ObjectID from 'bson-objectid';
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit {
  lists: List[] = [];
  tasks: Task[] = [];
  listId: any;
  listName!: string;
  taskName!: string;

  dashboardItems: items[] = [
    {
      icon: "fa-regular fa-sun",
      name: "Daily",
    },
    {
      icon: "fa-solid fa-house",
      name: "Tasks"
    },
    {
      icon: "fa-regular fa-star",
      name: "Important",
    }
  ]

  constructor(
    private taskService: TaskService,
    private route: ActivatedRoute,
    private router: Router) { };

  ngOnInit(): void {
    this.taskService.getLists()
      .subscribe((lists: any) => this.lists = lists);

    this.route.params.subscribe((params:Params) => {
      this.listId = params['listId'];
      if (!this.listId) return;
      this.taskService.getTasks(this.listId).subscribe((tasks: any) => this.tasks = tasks);
    })
  }

  addList(): any {
    this.taskService.createList(this.listName)
      .subscribe((list: any) => this.lists.push(list) )
  }

  addTask(): any{
    this.taskService.createTask(this.listId, this.taskName)
      .subscribe((task: any) => this.tasks.push(task))
  }

  onTaskClick(task: any) {
    this.taskService.setCompleted(this.listId, task).subscribe(
      () => task.completed = !task.completed
    );
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <title>Dashboard</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet"
    integrity="sha384-9ndCyUaIbzAi2FUVXJi0CjmCapSmO7SnpJef0486qhLnuZ2cdeRhO02iuK6FUUVM" crossorigin="anonymous">
  <script defer src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"
    integrity="sha384-geWF76RCwLtnZ8qwWowPQNguL3RmwHVBC9FhGdlKrxdiJJigb/j/68SIy3Te4Bkz" crossorigin="anonymous"></script>
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
    integrity="sha512-DTOQO9RWCH3ppGqcWaEA1BIZOC6xxalwEsw9c2QQeAIftl+Vegovlnee1c9QX4TctnWMn13TZye+giMm8e2LwA=="
    crossorigin="anonymous" referrerpolicy="no-referrer" />
</head>

<body>
  <div class="container-fluid overflow-hidden">
    <div class="row g-3 p-2">
      <div class="col-4 p-2 rounded-4">
        <h2><i class="fa-solid fa-clipboard-check"></i> To-Do List</h2>

        <div class="input-group mb-3">
          <input type="text" class="form-control" placeholder="Search">
          <span class="input-group-text" id="search"><i class="fa-solid fa-search"></i></span>
        </div>

        <div *ngFor="let item of dashboardItems">
          <a href="" class="btn btn-lg fs-5"><i class={{item.icon}}></i>  {{item.name}}</a>
        </div>
        <hr>

        <ng-container *ngFor="let list of lists">
          <a class="btn border-0 fs-5" [routerLink]="['/dashboard/lists',list._id,'tasks']" routerLinkActive="selected">
            <i class="fa-solid fa-bars border-black"></i>  {{list.title}}
          </a>
        </ng-container>

        <div class="input-group mb-3">
          <a class="btn btn" id="listBtn" [routerLink]="['/dashboard/lists']" (click)="addList()"><i class="fa-solid fa-plus"></i></a>
          <input type="text" class="form-control" placeholder="New List" data-bs-toggle="tooltip"
          data-bs-placement="top" data-bs-title="Add a new list" [(ngModel)]="listName">
        </div>
      </div>

      <div class="col-8 rounded-4">
        <div class="container-fluid">
          <ng-container *ngIf="tasks.length>0; else elseBlock">
            <h2>Tasks</h2>
            <ng-container *ngFor="let task of tasks" [style.color]="task.completed ? 'red' :'white'">
              <p>{{task.title}}</p>
            </ng-container>
          </ng-container>

          <ng-template #elseBlock>
            <h2 class="text-center">No Tasks are added yet!</h2>
          </ng-template>

          <div class="input-group mb-3">
            <a class="btn " id="taskBtn" [routerLink]="['/dashboard/lists',listId,'tasks']" (click)="addTask()"><i
                class="fa-solid fa-plus"></i></a>
            <input type="text" class="form-control" placeholder="New Task" data-bs-toggle="tooltip" data-bs-placement="top"
              data-bs-title="Add a new task" [(ngModel)]="taskName">
          </div>
        </div>
      </div>
    </div>
  </div>


  <div class="container-fluid">
    <h3 *ngIf="tasks.length>0">Tasks</h3>
    <button *ngFor="let task of tasks" class="btn btn-secondary d-block m-3" (click)="onTaskClick(task)"
      [style.color]="task.completed ? 'red' :'white'">
      {{task.title}}
    </button>
  </div>
</body>

</html>
