import { Component,OnInit } from '@angular/core';
import { Router, ActivatedRoute, Params } from '@angular/router';
import { TaskService } from '../task.service';
import List from '../models/list';
import Task from '../models/task';
import { items } from './dashboard';
@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent implements OnInit{
  lists: List[] = [];
  tasks: Task[] = [];
  listId: string = "";

  dashboardItems: items[] = [
    {
      icon: "fa-regular fa-sun",
      name: "Daily",
    },
    {
      icon: "fa-solid fa-house",
      name: "Tasks"
    },
    {
      icon: "fa-regular fa-star",
      name: "Important",
    },
    {
      icon: "fa-regular fa-file-lines",
      name: "Planned",
    }
  ]

  constructor(
    private taskService: TaskService,
    private route: ActivatedRoute,
    private router: Router) { };

  ngOnInit(): void {
    this.taskService.getLists()
      .subscribe((lists: any) => this.lists = lists);

    this.route.params.subscribe((params: Params) => {
      this.listId = params['listId'];
      if (!this.listId) return;
      this.taskService.getTasks(this.listId).subscribe((tasks: any) => this.tasks = tasks);
    })
  }

  onTaskClick(task: any) {
    this.taskService.setCompleted(this.listId, task).subscribe(
      () => task.completed = !task.completed
    );
  }
}
<!DOCTYPE html>
<html lang="en">

<head>
  <title>Dashboard</title>
  <link href="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/css/bootstrap.min.css" rel="stylesheet"
    integrity="sha384-9ndCyUaIbzAi2FUVXJi0CjmCapSmO7SnpJef0486qhLnuZ2cdeRhO02iuK6FUUVM" crossorigin="anonymous">
  <script defer src="https://cdn.jsdelivr.net/npm/bootstrap@5.3.0/dist/js/bootstrap.bundle.min.js"
    integrity="sha384-geWF76RCwLtnZ8qwWowPQNguL3RmwHVBC9FhGdlKrxdiJJigb/j/68SIy3Te4Bkz" crossorigin="anonymous"></script>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.5.1/css/all.min.css"
      integrity="sha512-DTOQO9RWCH3ppGqcWaEA1BIZOC6xxalwEsw9c2QQeAIftl+Vegovlnee1c9QX4TctnWMn13TZye+giMm8e2LwA=="
      crossorigin="anonymous" referrerpolicy="no-referrer" />
</head>

<body>
  <div class="container-fluid">
    <div class="row">
      <div class="col-4 gx-4 rounded-4">
        <h2><i class="fa-solid fa-clipboard-check"></i> To-Do List</h2>

        <div class="input-group mb-3">
          <input type="text" class="form-control" placeholder="Search">
          <span class="input-group-text" id="search"><i class="fa-solid "></i></span>
        </div>

        <ng-container>
            <div *ngFor="let item of dashboardItems" class="items">
              <a href="" class="btn"><i class={{item.icon}}></i> {{item.name}}</a>
            </div>
        </ng-container>
      </div>
      <div class="col-8 gx-4 rounded-4">
        <p>Everyone</p>
      </div>
    </div>
  </div>

